
public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int age = 16;

switch(age){

case 13:
	System.out.println ("teenager");
	break;
case 18:
	System.out.println("major");
	break;
case 20:
	System.out.println ("Youngster");
	break;
	
default:
	System.out.println ("Adult");
	
}

	}

}
